import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import './CreateHabit.css';

const CreateHabit = () => {
  const { user } = useAuth();
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    name: '',
    description: '',
    daysOfWeek: [],
    stakeAmount: 5
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [aiMessage, setAiMessage] = useState('');

  const daysOfWeek = [
    'Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'
  ];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleDayToggle = (day) => {
    setFormData(prev => ({
      ...prev,
      daysOfWeek: prev.daysOfWeek.includes(day)
        ? prev.daysOfWeek.filter(d => d !== day)
        : [...prev.daysOfWeek, day]
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!user?.isWalletConnected) {
      setError('Please connect your wallet before creating habits');
      return;
    }

    if (formData.daysOfWeek.length === 0) {
      setError('Please select at least one day of the week');
      return;
    }

    if (!formData.name.trim()) {
      setError('Please enter a habit name');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const response = await axios.post('/habits', {
        name: formData.name.trim(),
        description: formData.description.trim(),
        daysOfWeek: formData.daysOfWeek,
        stakeAmount: parseFloat(formData.stakeAmount)
      });

      if (response.data.aiMessage) {
        setAiMessage(response.data.aiMessage);
      }

      // Redirect to dashboard after a short delay to show AI message
      setTimeout(() => {
        navigate('/dashboard');
      }, 3000);

    } catch (error) {
      console.error('Error creating habit:', error);
      setError(error.response?.data?.error || 'Failed to create habit');
    } finally {
      setLoading(false);
    }
  };

  const getDayAbbreviation = (day) => {
    return day.slice(0, 3);
  };

  const getTotalWeeklyStake = () => {
    return (formData.daysOfWeek.length * formData.stakeAmount).toFixed(2);
  };

  if (!user?.isWalletConnected) {
    return (
      <div className="create-habit-container">
        <div className="wallet-required">
          <div className="wallet-required-icon">🦊</div>
          <h2>Wallet Required</h2>
          <p>You need to connect your wallet before creating habits.</p>
          <p>Your crypto will be at stake to keep you accountable!</p>
        </div>
      </div>
    );
  }

  return (
    <div className="create-habit-container">
      <div className="create-habit-card">
        <div className="create-habit-header">
          <h1>Create New Habit</h1>
          <p>Put your crypto where your commitment is! 💪</p>
        </div>

        {aiMessage && (
          <div className="ai-message">
            <div className="ai-icon">🤖</div>
            <div className="ai-content">
              <strong>Eliza says:</strong> {aiMessage}
            </div>
          </div>
        )}

        <form onSubmit={handleSubmit} className="create-habit-form">
          {error && <div className="error-message">{error}</div>}

          <div className="form-group">
            <label htmlFor="name">Habit Name *</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              placeholder="e.g., Go to the gym, Read for 30 minutes, Meditate"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="description">Description (Optional)</label>
            <textarea
              id="description"
              name="description"
              value={formData.description}
              onChange={handleInputChange}
              placeholder="Add more details about your habit..."
              rows={3}
            />
          </div>

          <div className="form-group">
            <label>Days of the Week *</label>
            <div className="days-selector">
              {daysOfWeek.map(day => (
                <button
                  key={day}
                  type="button"
                  className={`day-button ${formData.daysOfWeek.includes(day) ? 'selected' : ''}`}
                  onClick={() => handleDayToggle(day)}
                >
                  {getDayAbbreviation(day)}
                </button>
              ))}
            </div>
            <small className="form-help">
              Select the days you want to track this habit
            </small>
          </div>

          <div className="form-group">
            <label htmlFor="stakeAmount">Stake Amount (MATIC per missed day) *</label>
            <div className="stake-input-group">
              <input
                type="number"
                id="stakeAmount"
                name="stakeAmount"
                value={formData.stakeAmount}
                onChange={handleInputChange}
                min="0.1"
                step="0.1"
                required
              />
              <span className="currency-label">MATIC</span>
            </div>
            <small className="form-help">
              This amount will be at risk each day you miss your habit
            </small>
          </div>

          {formData.daysOfWeek.length > 0 && (
            <div className="stake-summary">
              <div className="summary-item">
                <span>Days per week:</span>
                <span>{formData.daysOfWeek.length}</span>
              </div>
              <div className="summary-item">
                <span>Stake per day:</span>
                <span>{formData.stakeAmount} MATIC</span>
              </div>
              <div className="summary-item total">
                <span>Max weekly risk:</span>
                <span>{getTotalWeeklyStake()} MATIC</span>
              </div>
            </div>
          )}

          <div className="stake-warning">
            <div className="warning-icon">⚠️</div>
            <div className="warning-content">
              <strong>How it works:</strong>
              <ul>
                <li>First miss: Your stake goes to a holding contract</li>
                <li>Second miss: All staked MATIC goes to charity</li>
                <li>7-day streak: Get your stake back + bonus rewards!</li>
              </ul>
            </div>
          </div>

          <div className="form-actions">
            <button
              type="button"
              onClick={() => navigate('/dashboard')}
              className="cancel-btn"
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={loading || formData.daysOfWeek.length === 0}
              className="create-btn"
            >
              {loading ? 'Creating...' : 'Create Habit'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreateHabit;
